import { saveAs } from 'file-saver';
import React, { useState, useEffect } from "react";
import Header from '../components/Header';
import Navigation from '../components/Navigation';
import Footer from '../components/Footer';
import mergeImages from 'merge-images';


/*
Component that displays the various components displayed on the song page
including the header, the navigation buttons, the image, and a button
to download the image
*/
const Song = () => {

    const [src, setSrc] = useState(null);

    /*
    Function that gets a user's top 20 songs from the backend /toptracks endpoint and puts the song names
    into an array
    */
    const apiGet = () => {
        return new Promise(
            (resolve) => {
                fetch('http://localhost:8888/toptracks', {
                    method: 'GET',
                    headers: {
                        'Access-Control-Allow-Origin': '*',
                        'Access-Control-Allow-Methods': 'GET,PUT,POST,DELETE,PATCH,OPTIONS',
                    }
                })
                    .then((response) => response.json())
                    .then(function (json) {
                        const songs = [];
                        for (let i = 0; i < 20; i++) {
                            songs.push(json[i].name);
                            if (i === 19) {
                                resolve(songs);
                            }
                        }
                    })
            });
    }


    //call to the apiGet function for song information
    var apicall = apiGet();

    /* 
    Function taking in url, width, and height of an image and superimposes
    text-- specifically a list of songs onto the image
    It returns a promise that resolves to a base64 string
    */
    const addSongArt = (url, width, height, get) => {
        var names = get ? get.map((item) => item) : null;
        return new Promise(
            (resolve) => {
                var img = new Image();
                img.src = url;
                img.setAttribute('crossOrigin', 'anonymous');

                img.onload = () => {
                    var canvas = document.createElement("canvas");
                    canvas.width = width * 1.25;
                    canvas.height = height * 1.25;
                    var ctx = canvas.getContext("2d");
                    ctx.drawImage(img, 0, 0, width * 1.25, height * 1.25);
                    ctx.font = "8.5pt Courier New";
                    let idx = 0;
                    for (let i = 0; i < 5; i++) {
                        for (let j = 0; j < 2; j++) {
                            ctx.fillText(names[idx], 97 + 142 * j, 87 + 42 * i, 75);
                            idx++;
                        }
                        for (let j = 2; j < 4; j++) {
                            ctx.fillText(names[idx], 87 + 140 * j, 87 + 42 * i, 75);
                            idx++;
                        }
                    }
                    resolve(canvas.toDataURL("image/png"));
                }

            });

    }

    //waits until the API call is made to add the song text on the images
    Promise.resolve(apicall).then((songs) => {
        const promise = addSongArt("https://i.postimg.cc/fRFr2V2W/jukebox.png", 540, 360, songs);
        promise.then((src) => setSrc(src))
    });

    // loading placehoder image
    useEffect(() => {
        mergeImages([
            'data:image/png;base64,'], { height: 450 })
            .then((src) => setSrc(src))
            .catch(error => console.log(error))
    }, []);

    // downloads merged image as png upon button click
    const downloadImage = () => {
        saveAs(src, "song-art.png");
    }


    return (
        <div>
            <Header />
            <Navigation />
            <img className="art" src={src} alt=""></img>
            <button className="btn2" type="button" onClick={downloadImage}>Download Image</button>
            <Footer></Footer>
        </div>
    );

}

export default Song;